<?php
/** 
 * 
 * @param string $str   UTF-8 encoding 
 * @param int[option] $width  
 * @param string[option] $end  
 * @param float[option] $x3
 * 3coefficient 
 * ,ascii, 
 * @return string 
 */ 

function utf8_substr($str, $width = 0, $end = '...', $x3 = 0) {  
    global $CFG; //  x3   
    if ($width <= 0 || $width >= strlen($str)) {  
        return $str;  
    }
    // 3
    $arr = str_split($str); 
    $len = count($arr);
    // 
    $strWidth = 0;
    $width *= 10;  

    //   
    $x1 = 11;   // ASCII  
    $x2 = 16;  
    $x3 = $x3===0 ? ( $CFG['cf3']  > 0 ? $CFG['cf3']*10 : $x3 = 21 ) : $x3*10;  
    $x4 = $x3;
    $endStr = '';
    $offset = 0;
    for ($offset = 0; $offset < $len; $offset++) {  
        if ($strWidth >= $width) {  
            $endStr = $end;  
            break;  
        }
        //  ASCII 
        $character = ord($arr[$offset]);  
        if ($character <= 127) {  
            $strWidth += $x1;  
        }  
        elseif ($character >= 192 && $character <= 223) { // 2  
            $strWidth += $x2;  
            $offset += 1;  
        }  
        elseif ($character >= 224 && $character <= 239) { // 3  
            $strWidth += $x3;  
            $offset += 2;  
        }  
        elseif ($character >= 240 && $character <= 247) { // 4  
            $strWidth += $x4;  
            $offset += 3;  
        }  
    }  

    return implode('', array_slice($arr, 0, $offset) ). $endStr;  
}
// test
$str1 = 'Celine Dion My Love Ultimate Essential Collection My Heart Will Go On';
$str2 = '';
$str3 = 'dfd';
echo "<br />";
echo utf8_substr($str1, 10);
echo "<hr />";
echo utf8_substr($str2, 10);
echo "<hr />";
echo utf8_substr($str3, 10);
